generated.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
ul,
li {
  list-style: none;
}

.image {
  text-align: center;
}

img {
  width: 280px;
  margin-top: 100px;
  pointer-events: none;
  /* 图片不可查看 */
}

.globalHeader {
  position: relative;
  margin-top: 20px;
  width: 561px;
  margin-left: auto;
  margin-right: auto;
}

.searchForm {}

input {
  width: 100%;
  padding: 13px;
  font-family: inherit;
  font-size: inherit;
  border-radius: 30px;
  padding-inline-end: 44px;
  padding-inline-start: 52px;
  border: none;
  box-shadow: 0px 0px 4px grey;
}

input:focus {
  outline: none;
}

.allsearchicon {
  position: absolute;
  top: 0;
  width: 100%;
  padding: 12px 20px;
  display: flex;
  justify-content: space-between;
  pointer-events: none;
}

.searchright {
  display: flex;
}

.searchChange {
  font-size: smaller;
  color: grey;
  margin-top: 3px;
}

.searchChangeLogo>.icon {
  color: grey;
  width: 1em;
  margin-left: 5px;
}

.globalMain {
  max-width: 620px;
  margin-left: auto;
  margin-right: auto;
}

.siteList {
  margin: 20px;
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
}

.siteList>li {
  width: 112px;
  height: 112px;
}

@media (max-width: 500px) {
  .image>img {
    width: 200px;
  }
  .globalHeader {
    position: relative;
    width: 300px;
    margin-top: 20px;
  }
}

.siteList>li:hover {
  background: rgb(229, 231, 232);
  border-radius: 5px;
  cursor: pointer;
}

.site {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  position: relative;
}

.close {
  border-radius: 50%;
  position: absolute;
  top: 0;
  right: 0;
  margin-top: 5px;
  display: none;
}

.site:hover .close {
  display: block;
  /* 鼠标浮上去显示 */
}
.site:hover .close > .icon {
  width: 24px;
  height: 24px;
  color: gray;
}

.delete1 {
  justify-content: center;
  background-color: #eee;
  border-radius: 50%;
  position: absolute;
  top: 0;
  left: 8px;
  width: 20px;
  height: 20px;
}

.delete1>.icon {
  width: 16px;
  height: 16px;
  margin: 2px;
}

.site>.logo {
  width: 48px;
  height: 48px;
  margin-top: 18px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgb(241, 243, 244);
}

.site>.logo>.icon {
  width: 24px;
  height: 24px;
}

.site>.link {
  margin: 10px;
  font-size: small;
}

.addButton {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

.last {}

.iconadd {
  width: 48px;
  height: 48px;
  margin-top: 18px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background: rgb(241, 243, 244);
}

.iconadd>.icon {}

.text {
  white-space: nowrap;
  margin: 10px;
  font-size: smaller;
}
